dt-example.css:
table.dataTable thead span.dt-order-number {
	color: white;
	background: #db5c00;
}

:root.dark,
:root[data-theme=dark],
:root[data-bs-theme=dark] {
	table.dataTable thead span.dt-order-number {
		color: black;
		background: #9dbf23;
	}
}

dt-example.js-vanilla:
new DataTable('#example', {
	order: [
		{idx: 0, dir: 'asc'},
		{idx: 1, dir: 'asc'}
	],
	orderNumbers: true
});
